import "antd/lib/message/style/css";
import _message from "antd/lib/message";
import { useState, useEffect } from "react";
import { myApiEndPoint, myApiAxiosInstance } from "../../constants/myApi.constant";
import { errorSelector } from "../../helpers/utils.helper"; // eslint-disable-next-line import/prefer-default-export

export const useFetchLiker = track => {
  const {
    0: likers,
    1: setLikers
  } = useState(null);
  const {
    0: loading,
    1: setLoading
  } = useState(true);
  useEffect(() => {
    const fetchLiker = async () => {
      try {
        const res = await myApiAxiosInstance.get(`${myApiEndPoint.PEOPLE_WHO_LIKES_TRACK}${track.id}`);
        setLikers(res.data);
        setLoading(false);
      } catch (error) {
        setLoading(false);

        _message.error(errorSelector(error));
      }
    };

    if (!track.count_like) return;
    fetchLiker();
  }, []);
  return {
    likers,
    loading
  };
};
